pub enum StdError {

    /// Whenever there is no specific error type available
    #[error("Generic error: {msg}")]
    GenericErr {
        msg: String,
    },

    #[error("Invalid Base64 string: {msg}")]
    InvalidBase64 {
        msg: String,
    },

    #[error("Invalid data size: expected={expected} actual={actual}")]
    InvalidDataSize {
        expected: u64,
        actual: u64,
    },

    /// Whenever UTF-8 bytes cannot be decoded into a unicode string, e.g. in String::from_utf8 or str::from_utf8.
    #[error("Cannot decode UTF8 bytes into string: {msg}")]
    InvalidUtf8 {
        msg: String,
    },

    #[error("{kind} not found")]
    NotFound {
        kind: String,
    },

    #[error("Error parsing into type {target_type}: {msg}")]
    ParseErr {
        /// the target type that was attempted
        target_type: String,
        msg: String,
    },

    #[error("Error serializing type {source_type}: {msg}")]
    SerializeErr {
        /// the source type that was attempted
        source_type: String,
        msg: String,
    },

    #[error("Cannot subtract {subtrahend} from {minuend}")]
    Underflow {
        minuend: String,
        subtrahend: String,
    },
}

pub enum SystemError {
    InvalidRequest {
        error: String,
        request: Binary,
    },
    InvalidResponse {
        error: String,
        response: Binary,
    },
    NoSuchContract {
        addr: HumanAddr,
    },
    Unknown {
    },
    UnsupportedRequest {
        kind: String,
    },
}

pub enum QueryRequest<C: CustomQuery> {
    Bank(BankQuery),
    Custom(C),
    Staking(StakingQuery),
    Wasm(WasmQuery),
}

pub enum BankQuery {

    /// This calls into the native bank module for one denomination
    /// Return value is BalanceResponse
    Balance {
        address: HumanAddr,
        denom: String,
    },

    /// This calls into the native bank module for all denominations.
    /// Note that this may be much more expensive than Balance and should be avoided if possible.
    /// Return value is AllBalanceResponse.
    AllBalances {
        address: HumanAddr,
    },
}

pub enum WasmQuery {

    /// this queries the public API of another contract at a known address (with known ABI)
    /// return value is whatever the contract returns (caller should know)
    Smart {
        contract_addr: HumanAddr,
        /// msg is the json-encoded QueryMsg struct
        msg: Binary,
    },

    /// this queries the raw kv-store of the contract.
    /// returns the raw, unparsed data stored at that key, which may be an empty vector if not present
    Raw {
        contract_addr: HumanAddr,
        /// Key is the raw key used in the contracts Storage
        key: Binary,
    },
}

pub enum StakingQuery {

    /// Returns the denomination that can be bonded (if there are multiple native tokens on the chain)
    BondedDenom {
    },

    /// AllDelegations will return all delegations by the delegator
    AllDelegations {
        delegator: HumanAddr,
    },

    /// Delegation will return more detailed info on a particular
    /// delegation, defined by delegator/validator pair
    Delegation {
        delegator: HumanAddr,
        validator: HumanAddr,
    },

    /// Returns all registered Validators on the system
    Validators {
    },
}

pub enum ContractResult<S> {
    Ok(S),

    /// An error type that every custom error created by contract developers can be converted to.
    /// This could potientially have more structure, but String is the easiest.
    #[serde(rename = "error")]
    Err(String),
}

pub enum BankMsg {
    Send {
        from_address: HumanAddr,
        to_address: HumanAddr,
        amount: Vec<Coin>,
    },
}

pub enum StakingMsg {
    Delegate {
        validator: HumanAddr,
        amount: Coin,
    },
    Undelegate {
        validator: HumanAddr,
        amount: Coin,
    },
    Withdraw {
        validator: HumanAddr,
        /// this is the "withdraw address", the one that should receive the rewards
        /// if None, then use delegator address
        recipient: Option<HumanAddr>,
    },
    Redelegate {
        src_validator: HumanAddr,
        dst_validator: HumanAddr,
        amount: Coin,
    },
}

pub enum WasmMsg {

    /// this dispatches a call to another contract at a known address (with known ABI)
    Execute {
        contract_addr: HumanAddr,
        /// msg is the json-encoded HandleMsg struct (as raw Binary)
        msg: Binary,
        send: Vec<Coin>,
    },

    /// this instantiates a new contracts from previously uploaded wasm code
    Instantiate {
        code_id: u64,
        /// msg is the json-encoded InitMsg struct (as raw Binary)
        msg: Binary,
        send: Vec<Coin>,
        /// optional human-readbale label for the contract
        label: Option<String>,
    },
}
